import React, { useState } from 'react';
import '../styles/userform.css'; // Import the CSS file

const JobForm = () => {
  const [jobTitle, setJobTitle] = useState('');
  const [jobType, setJobType] = useState('');
  const [jobDescription, setJobDescription] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [companyURL, setCompanyURL] = useState('');
  const [workType, setWorkType] = useState('');
  const [payScale, setPayScale] = useState('');
  const [skills, setSkills] = useState([]);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Construct the job object with form input values
    const job = {
      jobTitle,
      jobType,
      jobDescription,
      companyName,
      companyURL,
      workType,
      payScale,
      skills: skills.split(',').map((skill) => skill.trim()),
    };

    try {
      const response = await fetch('/api/jobs/add-job', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(job),
      });

      if (response.status === 201) {
        // Successfully created the job
        // You can add any success handling here
        console.log('Job created successfully');
      } else {
        // Handle errors here
        const data = await response.json();
        setError(data.error);
      }
    } catch (error) {
      console.error('Error creating job:', error);
      setError('An error occurred while creating the job.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h1>Add a New Job</h1>
      <label>Job Title</label>
      <input type="text" value={jobTitle} onChange={(e) => setJobTitle(e.target.value)} required />
      <label>Job Type</label>
      <input type="text" value={jobType} onChange={(e) => setJobType(e.target.value)} required />
      <label>Job Description</label>
      <textarea value={jobDescription} onChange={(e) => setJobDescription(e.target.value)} required />
      <label>Company Name</label>
      <input type="text" value={companyName} onChange={(e) => setCompanyName(e.target.value)} required />
      <label>Company URL</label>
      <input type="text" value={companyURL} onChange={(e) => setCompanyURL(e.target.value)} required />
      <label>Work Type</label>
      <input type="text" value={workType} onChange={(e) => setWorkType(e.target.value)} required />
      <label>Pay Scale</label>
      <input type="text" value={payScale} onChange={(e) => setPayScale(e.target.value)} required />
      <label>Skills Required</label>
      <input
        type="text"
        value={skills}
        onChange={(e) => setSkills(e.target.value)}
        placeholder="Separate skills with commas (e.g., HTML, CSS, JavaScript)"
        required
      />
      {error && <div className="error">{error}</div>}
      <button type="submit">Add Job</button>
    </form>
  );
};

export default JobForm;
