import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';

const SkillDevelopmentDetails = () => {
  const { id } = useParams();
  const { allSkillDevelopments, skillDevelopment, setSkillDevelopment, setAllSkillDevelopments, isEditing, setIsEditing } = useSkillDevelopmentContext();

  useEffect(() => {
    const fetchSkillDevelopmentById = async () => {
      try {
        // Fetch all skill developments
        const allSkillDevelopmentResponse = await fetch('http://localhost:4444/skill-development');
        if (allSkillDevelopmentResponse.ok) {
          const allSkillDevelopmentData = await allSkillDevelopmentResponse.json();
          setAllSkillDevelopments(allSkillDevelopmentData.skillDevelopment);
        } else {
          console.error('Failed to fetch all skill developments');
        }

        // If an ID is specified, fetch details for that specific skill development
        if (id) {
          const skillDevelopmentResponse = await fetch(`http://localhost:4444/skill-development/${id}`);
          if (skillDevelopmentResponse.ok) {
            const skillDevelopmentData = await skillDevelopmentResponse.json();
            setSkillDevelopment(skillDevelopmentData);
          } else {
            console.error('Failed to fetch skill development by ID');
          }
        }
      } catch (error) {
        console.error('Error fetching skill development data:', error.message);
      }
    };

    // Fetch data
    fetchSkillDevelopmentById();
  }, [id, setSkillDevelopment, setAllSkillDevelopments]);

  // Function to handle update
  const handleUpdate = async (skillId) => {
    try {
      // Fetch the skill details for the given ID
      const response = await fetch(`http://localhost:4444/skill-development/update-skill-development/${skillId}`);
      if (response.ok) {
        const data = await response.json();
        // Perform your update logic with the skill data
        console.log('Update Skill:', data);
      } else {
        console.error('Failed to fetch skill details for update');
      }
    } catch (error) {
      console.error('Error updating skill:', error.message);
    }
  };

  const handleDelete = async (skillId) => {
    try {
      const response = await fetch(`http://localhost:4444/skill-development/delete-skill-development/${skillId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Handle successful deletion
        console.log('Skill deleted successfully');
        // Optionally, you can update the local state to reflect the deletion
        setAllSkillDevelopments((prevSkills) => prevSkills.filter((skill) => skill._id !== skillId));
      } else {
        // Handle deletion failure
        console.error('Failed to delete skill');
      }
    } catch (error) {
      console.error('Error deleting skill:', error.message);
    }
  };

  if (id && !skillDevelopment) {
    return <div>Loading skill details...</div>;
  }

  return (
    <div className="homedetails">
      <div className="skill-development-details">
        <div className="all-internships">
          <h2>Skill Development Details</h2>
          <div className="internship-card">
            <h3>{skillDevelopment.title}</h3>
            <p>{skillDevelopment.description}</p>
            <p>{`Instructor: ${skillDevelopment.courseinstructor}`}</p>
            <strong>Type:</strong>
            <p>{skillDevelopment.coursetype}</p>
          </div>
        </div>
      </div>
      {/* ... Other parts of the code */}
    </div>
  );
};

export default SkillDevelopmentDetails;
