import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { useJobContext } from '../hooks/JobContext';

const JobDetails = () => {
  const { id } = useParams();
  const { job, setJob, allJobs, setAllJobs } = useJobContext();
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    jobTitle: '',
    jobType: '',
    jobDescription: '',
    companyName: '',
    workType: '',
    payScale: '',
    branchesEligible: '',
  });
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const allJobsResponse = await fetch('http://localhost:4444/jobs');
        if (allJobsResponse.ok) {
          const allJobsData = await allJobsResponse.json();
          setAllJobs(allJobsData.jobs);
        } else {
          console.error('Failed to fetch all jobs');
        }

        if (id) {
          const jobResponse = await fetch(`http://localhost:4444/jobs/${id}`);
          if (jobResponse.ok) {
            const jobData = await jobResponse.json();
            setJob(jobData);
          } else {
            console.error('Failed to fetch job details');
          }
        }
      } catch (error) {
        console.error('Error fetching job data:', error.message);
      }
    };

    fetchData();
  }, [id, setJob, setAllJobs]);

  const handleEditClick = () => {
    setEditMode(true);
    setFormData({
      jobTitle: job.jobTitle,
      jobType: job.jobType,
      jobDescription: job.jobDescription,
      companyName: job.companyName,
      workType: job.workType,
      payScale: job.payScale,
      branchesEligible: job.branchesEligible.join(', '),
    });
  };

  const handleUpdate = async (jobId) => {
    try {
      const response = await fetch(`http://localhost:4444/jobs/${jobId}`);
      if (response.ok) {
        const data = await response.json();
        setJob(data);
        setEditMode(false);
        // Optionally, you can redirect to the job details page
        // history.push(`/job-details/${jobId}`);
      } else {
        console.error('Failed to fetch job details for update');
      }
    } catch (error) {
      console.error('Error updating job:', error.message);
    }
  };

  const handleDelete = async (jobId) => {
    try {
      const response = await fetch(`http://localhost:4444/jobs/delete-job/${jobId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Job deleted successfully');
        setAllJobs((prevJobs) => prevJobs.filter((job) => job._id !== jobId));
      } else {
        console.error('Failed to delete job');
      }
    } catch (error) {
      console.error('Error deleting job:', error.message);
    }
  };

  if (id && !job) {
    return <div>Loading job details...</div>;
  }

  return (
    <div>
      {id && (
        <div className="job-details-container">
          {editMode ? (
            <div>
              {/* Render a form for editing */}
              <input
                type="text"
                value={formData.jobTitle}
                onChange={(e) => setFormData({ ...formData, jobTitle: e.target.value })}
              />
              {/* Add fields for other job details */}
            </div>
          ) : (
            <div>
              <h4 className="job-title">{job.jobTitle}</h4>
              {/* Render other job details here */}
            </div>
          )}
        </div>
      )}

      <div className="all-jobs-container">
        <div className="job-cards">
          {allJobs.map((job) => (
            <div key={job._id} className="job-card">
              <h3>{job.jobTitle}</h3>
              {/* Render other job details here */}
              <div className="action-buttons">
                {editMode ? (
                  <span
                    className="save-button"
                    onClick={() => handleUpdate(job._id)}
                  >
                    Save
                  </span>
                ) : (
                  <span
                    className="edit-button"
                    onClick={() => handleEditClick()}
                  >
                    Edit
                  </span>
                )}
                <span
                  className="delete-button"
                  onClick={() => handleDelete(job._id)}
                >
                  üóëÔ∏è
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default JobDetails;
