// frontend/src/hooks/SkillDevelopmentContext.js

import { createContext, useContext, useState, useEffect } from 'react';

const SkillDevelopmentContext = createContext();

export const SkillDevelopmentProvider = ({ children }) => {
  const [allSkillDevelopments, setAllSkillDevelopments] = useState([]);
  const [skillDevelopment, setSkillDevelopment] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Fetch and set skill developments from the backend here
    const fetchSkillDevelopments = async () => {
      try {
        const response = await fetch('http://localhost:4444/skill-development');
        if (response.ok) {
          const data = await response.json();
          setAllSkillDevelopments(data.skillDevelopments);
        } else {
          console.error('Failed to fetch skill developments');
        }
      } catch (error) {
        console.error('Error fetching skill developments:', error.message);
      }
    };

    fetchSkillDevelopments();
  }, []);

  const value = {
    allSkillDevelopments,
    skillDevelopment,
    setSkillDevelopment,
    setAllSkillDevelopments,
    isEditing,
    setIsEditing,
  };

  return (
    <SkillDevelopmentContext.Provider value={value}>
      {children}
    </SkillDevelopmentContext.Provider>
  );
};

export const useSkillDevelopmentContext = () => {
  const context = useContext(SkillDevelopmentContext);
  if (!context) {
    throw new Error('useSkillDevelopmentContext must be used within a SkillDevelopmentProvider');
  }
  return context;
};
