import React, { useState, useEffect } from 'react';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';

const SkillDevelopmentForm = () => {
  const {
    setAllSkillDevelopments,
    skillDevelopment,
    isEditing,
    setIsEditing,
  } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courselink: '',
    pdfs: '',
    image: '',
  });

  useEffect(() => {
    if (isEditing && skillDevelopment) {
      setFormData({
        title: skillDevelopment.title,
        description: skillDevelopment.description,
        courselink: skillDevelopment.courselink.join(', '),
        pdfs: skillDevelopment.pdfs.join(', '),
        image: skillDevelopment.image.join(', '),
      });
    }
  }, [isEditing, skillDevelopment]);

  const handleArrayChange = (field, e) => {
    const values = e.target.value.split(',').map((value) => value.trim());
    setFormData({ ...formData, [field]: values });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const skillDevelopmentData = {
      title: formData.title,
      description: formData.description,
      courselink: Array.isArray(formData.courselink) ? formData.courselink : formData.courselink.split(',').map((link) => link.trim()),
    pdfs: Array.isArray(formData.pdfs) ? formData.pdfs : formData.pdfs.split(',').map((pdf) => pdf.trim()),
    image: Array.isArray(formData.image) ? formData.image : formData.image.split(',').map((image) => image.trim()),
    };

    try {
      let response;
      if (isEditing) {
        response = await fetch(`http://localhost:4444/skill-development/update/${skillDevelopment._id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(skillDevelopmentData),
        });
      } else {
        response = await fetch('http://localhost:4444/skill-development/add', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify(skillDevelopmentData),
});

      }

      if (response.ok) {
        const allSkillDevelopmentsResponse = await fetch('http://localhost:4444/skill-development');
        if (allSkillDevelopmentsResponse.ok) {
          const data = await allSkillDevelopmentsResponse.json();
          setAllSkillDevelopments(data.skillDevelopments);
        } else {
          console.error('Failed to fetch all skill developments');
        }

        setFormData({
          title: '',
          description: '',
          courselink: '',
          pdfs: '',
          image: '',
        });
        setIsEditing(false);

        console.log('Skill Development content added or updated successfully');
      } else {
        console.error('Failed to add or update skill development content');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="contained">
      <div className="skill-form">
        <h1>{isEditing ? 'Update Skill Development Content' : 'Add New Skill Development Content'}</h1>
        <form className="create" onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group">
              <label>Title:</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Description:</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              ></textarea>
            </div>
          </div>

          <div className="form-group">
            <label>Course Links:</label>
            <input
              type="text"
              className="input-field"
              value={formData.courselink}
              onChange={(e) => handleArrayChange('courselink', e)}
              placeholder="(e.g., Course Title: Link)"
              required
            />
          </div>

          <div className="form-group">
            <label>PDFs:</label>
            <input
              type="text"
              className="input-field"
              value={formData.pdfs}
              onChange={(e) => handleArrayChange('pdfs', e)}
              placeholder="(e.g., PDF Title: PDF Link)"
              required
            />
          </div>

          <div className="form-group">
            <label>Image:</label>
            <input
              type="text"
              className="input-field"
              value={formData.image}
              onChange={(e) => handleArrayChange('image', e)}
              placeholder="(e.g., Image for the course)"
              required
            />
          </div>

          <button type="submit">
            {isEditing ? 'Update Skill Development Content' : 'Add Skill Development Content'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SkillDevelopmentForm;
