import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';
import SkillDevelopmentForm from './SkillDevelopmentForm';

const SkillDevelopmentDetails = () => {
  const { id } = useParams();
  const { skillDevelopment, setSkillDevelopment, allSkillDevelopments, setAllSkillDevelopments, isEditing, setIsEditing } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courselink: [],
    pdfs: [],
    image: [],
  });

  const history = useHistory();

  useEffect(() => {
    const fetchSkillDevelopments = async () => {
      try {
        const response = await fetch('http://localhost:4444/skill-development');
        if (response.ok) {
          const data = await response.json();
          setAllSkillDevelopments(data.skillDevelopments);
        } else {
          console.error('Failed to fetch all skill developments');
        }
      } catch (error) {
        console.error('Error fetching all skill developments:', error.message);
      }
    };

    fetchSkillDevelopments();

    if (id) {
      const currentSkillDevelopment = allSkillDevelopments.find((skillDevelopment) => skillDevelopment._id === id);
      setSkillDevelopment(currentSkillDevelopment);

      setFormData({
        title: currentSkillDevelopment.title,
        description: currentSkillDevelopment.description,
        courselink: currentSkillDevelopment.courselink.join(', '),
        pdfs: currentSkillDevelopment.pdfs.join(', '),
        image: currentSkillDevelopment.image.join(', '),
      });
    }
  }, [id, setSkillDevelopment, setAllSkillDevelopments]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleUpdate = async (skillDevelopmentId) => {
    setIsEditing(true);

    try {
      const response = await fetch(`http://localhost:4444/skill-development/update/${skillDevelopmentId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title: formData.title,
          description: formData.description,
          courselink: formData.courselink.split(',').map((link) => link.trim()),
          pdfs: formData.pdfs.split(',').map((pdf) => pdf.trim()),
          image: formData.image.split(',').map((image) => image.trim()),
        }),
      });

      if (response.ok) {
        const updatedSkillDevelopment = await response.json();
        history.push('/admin/skillpage');
        setSkillDevelopment(updatedSkillDevelopment);
      } else {
        console.error('Failed to update skill development:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating skill development:', error.message);
    }
  };

  const handleDelete = async (skillDevelopmentId) => {
    try {
      const response = await fetch(`http://localhost:4444/skill-development/${skillDevelopmentId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setAllSkillDevelopments((prevSkillDevelopments) =>
          prevSkillDevelopments.filter((skillDevelopment) => skillDevelopment._id !== skillDevelopmentId)
        );
        console.log('Skill Development deleted successfully');
      } else {
        console.error('Failed to delete skill development');
      }
    } catch (error) {
      console.error('Error deleting skill development:', error.message);
    }
  };

  if (id && !skillDevelopment) {
    return <div>Loading skill development details...</div>;
  }

  return (
    <div className="homedetails">
      {id && (
        <div className="skill-development-details">
          {isEditing ? (
            <SkillDevelopmentForm isEditing={isEditing} setIsEditing={setIsEditing} initialFormData={formData} />
          ) : (
            <div>
              <h4>{skillDevelopment.title}</h4>
              <p>{skillDevelopment.description}</p>
              {/* Render other skill development details here */}
            </div>
          )}
          <button onClick={isEditing ? () => handleUpdate(skillDevelopment._id) : handleEditClick}>
            {isEditing ? 'Save' : 'Edit'}
          </button>
        </div>
      )}

      <div className="all-skill-developments">
        <h2>All Skill Developments</h2>
        <div className="skill-development-cards">
          {allSkillDevelopments.map((skillDevelopment) => (
            <div key={skillDevelopment._id} className="skill-development-card">
              <h3>{skillDevelopment.title}</h3>
              {/* Render other skill development details here */}

              <div className="action-buttons">
                <span
                  role="img"
                  aria-label="Update"
                  onClick={() => handleUpdate(skillDevelopment._id)}
                >
                  ✏️
                </span>
                <span
                  role="img"
                  aria-label="Delete"
                  onClick={() => handleDelete(skillDevelopment._id)}
                >
                  🗑️
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SkillDevelopmentDetails;
