import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';
import SkillDevelopmentForm from './SkillDevelopmentForm';

const SkillDevelopmentDetails = () => {
  const { id } = useParams();
  const {
    skillDevelopment,
    setSkillDevelopment,
    isEditing,
    setIsEditing,
  } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courseinstructor: '',
    coursetype: '',
  });

  const history = useHistory();

    useEffect(() => {
      const fetchSkillDevelopmentById = async () => {
        try {
          if (id) {
            const response = await fetch(`http://localhost:4444/skill-development/${id}`);
            if (response.ok) {
              const data = await response.json();
              setSkillDevelopment(data);
              setFormData({
                title: data.title,
                description: data.description,
                courseinstructor: data.courseinstructor,
                coursetype: data.coursetype,
              });
            } else {
              console.error('Failed to fetch skill development by ID');
            }
          }
        } catch (error) {
          console.error('Error fetching skill development by ID:', error.message);
        }
      };

      fetchSkillDevelopmentById();
    }, [id, setSkillDevelopment]);


  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleUpdate = async () => {
    try {
      const response = await fetch(
        `http://localhost:4444/skill-development/update-skill-development/${id}`,
        {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        }
      );

      if (response.ok) {
        const updatedSkillDevelopment = await response.json();
        setSkillDevelopment(updatedSkillDevelopment);
        setIsEditing(false);
      } else {
        console.error('Failed to update skill development:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating skill development:', error.message);
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:4444/skill-development/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Skill Development deleted successfully');
        history.push('/admin/skillpage');
      } else {
        console.error('Failed to delete skill development');
      }
    } catch (error) {
      console.error('Error deleting skill development:', error.message);
    }
  };

  if (!id) {
    return <div>No skill development ID provided.</div>;
  }

  if (!skillDevelopment && !isEditing) {
    return <div>Loading skill development details...</div>;
  }

  if (!skillDevelopment && isEditing) {
    return <div>Loading skill development for editing...</div>;
  }

  return (
    <div className="homedetails">
      <div className="skill-development-details">
        {isEditing ? (
          <SkillDevelopmentForm
            isEditing={isEditing}
            setIsEditing={setIsEditing}
            initialFormData={formData}
          />
        ) : (
          <div className="all-internships">
            <h2>All Internships</h2>
            <div className="internship-cards">
              <div className="internship-card">
                <h3>{formData.title}</h3>
                <p>{formData.description}</p>
                <p>{`Instructor: ${formData.courseinstructor}`}</p>
                <strong>Type:</strong>
                <p>{formData.coursetype}</p>
                {/* Edit and Delete buttons */}
                <div className="action-buttons">
                  <button onClick={handleEditClick}>Edit</button>
                  <button onClick={handleDelete}>Delete</button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      {/* ... Other parts of the code */}
    </div>
  );
};

export default SkillDevelopmentDetails;
