import React from 'react';
import './admin.css'; // Make sure you have the relevant CSS file

const AdminLogin = () => {
  return (
    <div className="container">
      <div className="image">
        <img src="images/pexels-photo-1181325.webp" alt="Admin Image" />
      </div>
      <div className="myform">
        <form>
          <h2>ADMIN LOGIN</h2>
          <input type="text" placeholder="Admin Name" />
          <input type="password" placeholder="Password" />
          <button type="submit">LOGIN</button>
        </form>
        <a href="forget-password">Forgot my password!</a>
      </div>
    </div>
  );
};

export default AdminLogin;
