// SkillsPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SkillsPage = () => {
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState({
    courseName: '',
    duration: '',
    courseLink: '',
    source: '',
    instructorName: '',
    mode: 'free', // Default to free
  });

  useEffect(() => {
    // Fetch skills when the component mounts
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      const response = await axios.get('http://localhost:4444/skills');
      setSkills(response.data);
    } catch (error) {
      console.error('Error fetching skills:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewSkill({
      ...newSkill,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4444/skills', newSkill);
      // Clear the form and fetch updated skills
      setNewSkill({
        courseName: '',
        duration: '',
        courseLink: '',
        source: '',
        instructorName: '',
        mode: 'free',
      });
      fetchSkills();
    } catch (error) {
      console.error('Error creating skill:', error);
    }
  };

  return (
    <div>
      <h2>Skill Development Page</h2>

      <form onSubmit={handleSubmit}>
        {/* Add form inputs for each skill attribute */}
        <label>
          Course Name:
          <input type="text" name="courseName" value={newSkill.courseName} onChange={handleInputChange} required />
        </label>
        {/* Add similar labels and inputs for other attributes */}
        {/* ... */}

        <button type="submit">Add Skill</button>
      </form>

      <div>
        <h3>Available Skills:</h3>
        {skills.length === 0 ? (
          <p>No skills available.</p>
        ) : (
          <ul>
            {skills.map((skill) => (
              <li key={skill._id}>
                {skill.courseName} - {skill.mode === 'free' ? 'Free' : 'Paid'}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default SkillsPage;
