import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/jobpage.css'; // Import the CSS file

const JobDetails = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const [allJobs, setAllJobs] = useState([]);

  // Function to handle job update
  const handleUpdate = async (jobId) => {
    try {
      const response = await fetch(`http://localhost:4444/jobs/${jobId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        // You can pass the updated job data in the body
        // Example: JSON.stringify({ jobTitle: 'New Title', jobType: 'New Type', ... })
        body: JSON.stringify(/* Updated job data */),
      });

      if (response.ok) {
        // Successfully updated the job
        console.log('Job updated successfully');
        // You may want to fetch all jobs again to update the list
        fetchAllJobs();
      } else {
        // Handle errors here
        const data = await response.json();
        console.error('Error updating job:', data.error);
      }
    } catch (error) {
      console.error('Error updating job:', error);
    }
  };

  // Function to handle job deletion
  const handleDelete = async (jobId) => {
    try {
      const response = await fetch(`http://localhost:4444/jobs/${jobId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Successfully deleted the job
        console.log('Job deleted successfully');
        // You may want to fetch all jobs again to update the list
        fetchAllJobs();
      } else {
        // Handle errors here
        const data = await response.json();
        console.error('Error deleting job:', data.error);
      }
    } catch (error) {
      console.error('Error deleting job:', error);
    }
  };

  // Function to fetch job details by ID
  const fetchJobDetails = async () => {
    try {
      const response = await fetch(`http://localhost:4444/jobs/${id}`);
      if (response.ok) {
        const data = await response.json();
        setJob(data.job);
      } else {
        console.error('Failed to fetch job details');
      }
    } catch (error) {
      console.error('Error fetching job details:', error.message);
    }
  };

  // Function to fetch all jobs
  const fetchAllJobs = async () => {
    try {
      const response = await fetch('http://localhost:4444/jobs');
      if (response.ok) {
        const data = await response.json();
        setAllJobs(data.jobs);
      } else {
        console.error('Failed to fetch all jobs');
      }
    } catch (error) {
      console.error('Error fetching all jobs:', error.message);
    }
  };

  useEffect(() => {
    fetchJobDetails();
    fetchAllJobs();
  }, [id]); // Fetch job details and all jobs whenever the ID changes

  if (!job) {
    return <div>Loading job details...</div>;
  }

  return (
    <div className="homedetail">
      {id && (
        <div className="job-details">
          <h4>{job.jobTitle}</h4>
          {/* Render other job details here */}
        </div>
      )}

      <div className="all-jobs">
        <h2>All Jobs</h2>
        <table>
          <thead>
            <tr>
              <th>Job Title</th>
              <th>Job Type</th>
              <th>Job Description</th>
              <th>Company Name</th>
              <th>Company URL</th>
              <th>Work Type</th>
              <th>Pay Scale</th>
              <th>Skills</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allJobs.map((job) => (
              <tr key={job._id}>
                <td>{job.jobTitle}</td>
                <td>{job.jobType}</td>
                <td>{job.jobDescription}</td>
                <td>{job.companyName}</td>
                <td>{job.companyURL}</td>
                <td>{job.workType}</td>
                <td>{job.payScale}</td>
                <td>{job.skills.join(', ')}</td>
                <td>
                  <span
                    role="img"
                    aria-label="Update"
                    style={{
                      cursor: 'pointer',
                      padding: '5px',
                      borderRadius: '50%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleUpdate(job._id)}
                  >
                    ‚úèÔ∏è
                  </span>
                  <span
                    role="img"
                    aria-label="Delete"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '5px',
                      borderRadius: '50%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleDelete(job._id)}
                  >
                    üóëÔ∏è
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default JobDetails;
