// InternshipDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useInternshipContext } from '../hooks/InternshipContext';
import EditableRow from './EditableRow';

const InternshipDetails = () => {
  const { id } = useParams();
  const { internship, setInternship, allInternships, setAllInternships } = useInternshipContext();
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchInternships = async () => {
      try {
        const response = await fetch('http://localhost:4444/internships');
        if (response.ok) {
          const data = await response.json();
          setAllInternships(data.internships);
        } else {
          console.error('Failed to fetch all internships');
        }
      } catch (error) {
        console.error('Error fetching all internships:', error.message);
      }
    };

    fetchInternships();

    if (id) {
      const currentInternship = allInternships.find((internship) => internship._id === id);
      setInternship(currentInternship);
    }
  }, [id, setInternship, setAllInternships]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelClick = () => {
    setIsEditing(false);
  };

  const handleEditFormChange = (e) => {
    const { name, value } = e.target;
    setInternship((prevInternship) => ({
      ...prevInternship,
      [name]: value,
    }));
  };

  const handleSaveClick = () => {
    setAllInternships((prevInternships) =>
      prevInternships.map((prevInternship) =>
        prevInternship._id === id ? { ...prevInternship, ...internship } : prevInternship
      )
    );

    setIsEditing(false);
  };

  if (id && !internship) {
    return <div>Loading internship details...</div>;
  }

  return (
    <div className="homedetail">
      {id && (
        <div className="internship-details">
          {isEditing ? (
            <EditableRow
              internship={internship}
              handleEditFormChange={handleEditFormChange}
              handleCancelClick={handleCancelClick}
              handleSaveClick={handleSaveClick}
            />
          ) : (
            <div>
              <h4>{internship.internshipTitle}</h4>
              {/* Render other internship details here */}
              <button onClick={handleEditClick}>Edit</button>
            </div>
          )}
        </div>
      )}

// ... (previous code)

<div className="all-internships">
  <h2>All Internships</h2>
  <table>
    <thead>
      <tr>
        <th>Internship Title</th>
        {/* Add other table headers */}
        <th>Internship Type</th>
        <th>Internship Description</th>
        <th>Company Name</th>
        <th>Company URL</th>
        <th>Duration</th>
        <th>Eligible Branches</th>
        <th>Action</th>
      </tr>
    </thead>
    <tbody>
      {allInternships.map((internship) => (
        <tr key={internship._id}>
          <td>{internship.internshipTitle}</td>
          <td>{internship.internshipType}</td>
          <td>{internship.internshipDescription}</td>
          <td>{internship.companyName}</td>
          <td>{internship.companyURL}</td>
          <td>{internship.duration}</td>
          <td>{internship.branchesEligible.join(', ')}</td>
          <td>
            <span
              role="img"
              aria-label="Update"
              style={{
                cursor: 'pointer',
                marginBottom: '3.5px',
                borderRadius: '40%',
                backgroundColor: '#e0e0e0',
                fontSize: '20px',
              }}
              onClick={() => handleUpdate(internship._id)}
            >
              ✏️
            </span>
            <span
              role="img"
              aria-label="Delete"
              style={{
                cursor: 'pointer',
                marginBottom: '3.5px',
                borderRadius: '40%',
                backgroundColor: '#e0e0e0',
                fontSize: '20px',
              }}
              onClick={() => handleDelete(internship._id)}
            >
              🗑️
            </span>
          </td>
        </tr>
      ))}
    </tbody>
  </table>
</div>
</div>
);
};

export default InternshipDetails;
