import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useInternshipContext } from '../hooks/InternshipContext'; // Update the path

const InternshipDetails = () => {
  const { id } = useParams();
  const { internship, setInternship, allInternships, setAllInternships } = useInternshipContext();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    // Define your form fields here
    internshipTitle: '',
    internshipType: '',
    internshipDescription: '',
    companyName: '',
    companyURL: '',
    duration: '',
    branchesEligible: [],
  });

  useEffect(() => {
    // Fetch all internships from the context when the component mounts
    const fetchInternships = async () => {
      try {
        const response = await fetch('http://localhost:4444/internships');
        if (response.ok) {
          const data = await response.json();
          setAllInternships(data.internships);
        } else {
          console.error('Failed to fetch all internships');
        }
      } catch (error) {
        console.error('Error fetching all internships:', error.message);
      }
    };

    // Fetch internships
    fetchInternships();

    // If an ID is specified, fetch details for that specific internship
    if (id) {
      const currentInternship = allInternships.find((internship) => internship._id === id);
      setInternship(currentInternship);
      // Set form data for editing
      setFormData({
        internshipTitle: currentInternship.internshipTitle,
        internshipType: currentInternship.internshipType,
        internshipDescription: currentInternship.internshipDescription,
        companyName: currentInternship.companyName,
        companyURL: currentInternship.companyURL,
        duration: currentInternship.duration,
        branchesEligible: currentInternship.branchesEligible.join(', '),
      });
    }
  }, [id, setInternship, setAllInternships]);

  // Function to handle update
  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:4444/internships/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const updatedInternship = await response.json();
        // Update the internship in the state
        setAllInternships((prevInternships) =>
          prevInternships.map((internship) =>
            internship._id === id ? { ...internship, ...updatedInternship } : internship
          )
        );
        // Clear form data and set editing to false
        setFormData({
          internshipTitle: '',
          internshipType: '',
          internshipDescription: '',
          companyName: '',
          companyURL: '',
          duration: '',
          branchesEligible: [],
        });
        setIsEditing(false);
      } else {
        console.error('Failed to update internship');
      }
    } catch (error) {
      console.error('Error updating internship:', error.message);
    }
  };

  // Function to handle delete
  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:4444/internships/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Remove the deleted internship from the state
        setAllInternships((prevInternships) =>
          prevInternships.filter((internship) => internship._id !== id)
        );
        // Clear the current internship details
        setInternship(null);
      } else {
        console.error('Failed to delete internship');
      }
    } catch (error) {
      console.error('Error deleting internship:', error.message);
    }
  };

  // Render loading message if ID is present but internship details are not loaded yet
  if (id && !internship) {
    return <div>Loading internship details...</div>;
  }

  return (
    <div className="homedetail">
      {id && (
        <div className="internship-details">
          <h4>{internship.internshipTitle}</h4>
          {/* Render other internship details here */}
        </div>
      )}

      <div className="all-internships">
        <h2>All Internships</h2>
        <table>
          <thead>
            <tr>
              <th>Internship Title</th>
              <th>Internship Type</th>
              <th>Internship Description</th>
              <th>Company Name</th>
              <th>Company URL</th>
              <th>Duration</th>
              <th>Eligible Branches</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allInternships.map((internship) => (
              <tr key={internship._id}>
                <td>{internship.internshipTitle}</td>
                <td>{internship.internshipType}</td>
                <td>{internship.internshipDescription}</td>
                <td>{internship.companyName}</td>
                <td>{internship.companyURL}</td>
                <td>{internship.duration}</td>
                <td>{internship.branchesEligible.join(', ')}</td>
                <td>
                  {/* Include your handleUpdate and handleDelete functions here */}
                  <span
                    role="img"
                    aria-label="Update"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px', // Adjust the font size as needed
                    }}
                    onClick={() => handleUpdate(internship._id)}
                  >
                    ‚úèÔ∏è
                  </span>
                  <span
                    role="img"
                    aria-label="Delete"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px', // Adjust the font size as needed
                    }}
                    onClick={() => handleDelete(internship._id)}
                  >
                    üóëÔ∏è
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InternshipDetails;
