// frontend/src/hooks/SkillDevelopmentContext.js

import { createContext, useContext, useState } from 'react';

const SkillDevelopmentContext = createContext();

export const SkillDevelopmentProvider = ({ children }) => {
  const [allSkillDevelopments, setAllSkillDevelopments] = useState([]);
  const [skillDevelopment, setSkillDevelopment] = useState(null);
  const [isEditing, setIsEditing] = useState(false);


  const value = {
    allSkillDevelopments,
    skillDevelopment,
    setSkillDevelopment,
    setAllSkillDevelopments,
    isEditing,
    setIsEditing,
  };

  return (
    <SkillDevelopmentContext.Provider value={value}>
      {children}
    </SkillDevelopmentContext.Provider>
  );
};

export const useSkillDevelopmentContext = () => {
  const context = useContext(SkillDevelopmentContext);
  if (!context) {
    throw new Error('useSkillDevelopmentContext must be used within a SkillDevelopmentProvider');
  }
  return context;
};
