import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/jobpage.css'; // Import the CSS file


  const JobDetails = () => {
    const { id } = useParams();
    const [job, setJob] = useState(null);

    useEffect(() => {
      // Fetch the job details by its ID from the backend
      const fetchJobDetails = async () => {
        try {
          const response = await fetch(`http://localhost:4444/jobs/${id}`);
          if (response.ok) {
            const data = await response.json();
            setJob(data.job);
          } else {
            console.error('Failed to fetch job details');
          }
        } catch (error) {
          console.error('Error fetching job details:', error.message);
        }
      };

      fetchJobDetails();
    }, [id]);

    if (!job) {
      return <div>Loading job details...</div>;
    }

  return (
    <div className="homedetail">
      <div className="job-details">
        <h4>{job.jobTitle}</h4>
        {/* Render other job details here */}
      </div>

      <div className="all-jobs">
        <h2>All Jobs</h2>
        <table>
          <thead>
            <tr>
              <th>Job Title</th>
              <th>Job Type</th>
              <th>Job Description</th>
              <th>Company Name</th>
              <th>Company URL</th>
              <th>Work Type</th>
              <th>Pay Scale</th>
              <th>Skills</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allJobs.map((job) => (
              <tr key={job._id}>
                <td>{job.jobTitle}</td>
                <td>{job.jobType}</td>
                <td>{job.jobDescription}</td>
                <td>{job.companyName}</td>
                <td>{job.companyURL}</td>
                <td>{job.workType}</td>
                <td>{job.payScale}</td>
                <td>{job.skills.join(', ')}</td>
                <td>
                  <button onClick={() => handleUpdate(job._id)}>Update</button>
                  <button onClick={() => handleDelete(job._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default JobDetails;
