// AdminDashboard.js
import React from 'react';
import { Link, Route, Switch } from 'react-router-dom';
import AddUser from './UserForm';
import AddInternship from './InternshipForm';
import AddJob from './JobForm';
import '../styles/index1.css';

const AdminDashboard = () => {
    return (
        <div>
          <h1>Welcome to Student Dashboard</h1>
          <p>Here, students can access their profile and view job/internship listings.</p>
          <header className="header">
            <div className="left-navbar">
            <p className="nav-title">CBIT - ADMIN DASHBOARD</p>
            </div>
        <div>
      <ul>
        <li>
        <Link to="/admin/add-student">Add User</Link>
        </li>
        <li>
        <Link to="/admin/add-internship">Add Internship</Link>
        </li>
        <li>
        <Link to="/admin/add-job">Add Job</Link>
        </li>
        {/* <li>
        <Link to="/admin/logout">Logout</Link>
        </li> */}
      </ul>
    </div>
    </header>

      <Switch>
        <Route path="/admin/add-user" component={AddUser} />
        <Route path="/admin/add-internship" component={AddInternship} />
        <Route path="/admin/add-job" component={AddJob} />
        {/* <Route path="/admin/logout" component={Logout} /> */}
      </Switch>
    </div>
  );
};

export default AdminDashboard;
