import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/jobpage.css'; // Import the CSS file

const InternshipDetails = () => {
  const { id } = useParams();
  const [internship, setInternship] = useState(null);

  useEffect(() => {
    // Fetch the internship details by its ID from the backend
    const fetchInternshipDetails = async () => {
      try {
        const response = await fetch(`/api/internships/${id}`);
        if (response.ok) {
          const data = await response.json();
          setInternship(data.internship);
        } else {
          console.error('Failed to fetch internship details');
        }
      } catch (error) {
        console.error('Error fetching internship details:', error);
      }
    };

    fetchInternshipDetails();
  }, [id]);

  if (!internship) {
    return <div>Loading internship details...</div>;
  }

  return (
    <div>
      <h1>{internship.internshipTitle}</h1>
      <p>Company: {internship.companyName}</p>
      <p>Type: {internship.internshipType}</p>
      <p>Description: {internship.internshipDescription}</p>
      <p>Duration: {internship.duration}</p>
      <p>Skills Required: {internship.skillsRequired.join(', ')}</p>
    </div>
  );
};

export default InternshipDetails;
