// Import necessary dependencies
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../otherstyles/admin.css';
import image from '../images/student.webp';

// Define apiUrl outside the component
// const apiUrl = 'http://localhost:4444';

export default function Student() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    try {
      const response = await fetch(`http://localhost:4444/users/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        history.push('/student/dashboard');
      } else {
        if (response.status === 400) {
          // Bad Request - Invalid credentials or other client-side error
          const errorText = await response.text();
          console.error('Login failed:', errorText);
          setErrorMessage('Invalid credentials. Please check your username and password.');
        } else {
          // Other server-side errors
          console.error('Login failed:', response.statusText);
          setErrorMessage('An unexpected error occurred during login.');
        }
      }
    } catch (error) {
      console.error('An unexpected error occurred during login:', error);
      setErrorMessage('An unexpected error occurred during login.');
    }
  };




  // Return the JSX structure of the component
  return (
    <div className="container">
      <div className="heading">
        <div className="image">
          <img src={image} alt="Student" />
          <h1>Student Login</h1>
        </div>
      </div>

      <input
        type="text"
        id="username"
        className="input"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <input
        type="password"
        id="password"
        className="input"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <div className="submit">
        <div className="button" onClick={handleLogin}>
          Login
        </div>
      </div>

      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
}
