// Import necessary dependencies
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import '../otherstyles/admin.css';
import image from '../images/student.webp';

// Define apiUrl outside the component
// const apiUrl = 'http://localhost:4444';

export default function Student() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    try {
      const response = await fetch(`http://localhost:4444/users/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      console.log('Request body:', { email, password });
      console.log('Response:', response);

      if (response.ok) {
        // Instead of trying to get the token from the response body,
        // extract it from the cookie
        const token = document.cookie.split('; ').find(row => row.startsWith('access_token=')).split('=')[1];

        // You can now use the token as needed, for example, save it to localStorage
        localStorage.setItem('token', token);

        history.push('/student/dashboard');
      } else {
        const errorText = await response.text();
        console.error('Login failed:', errorText);
        setErrorMessage('Invalid credentials. Please check your username and password.');
      }
    } catch (error) {
      console.error('Unexpected error during login:', error);
      // Handle the error appropriately, e.g., display a generic error message to the user
      setErrorMessage('An unexpected error occurred during login.');
    }
  };


  // Return the JSX structure of the component
  return (
    <div className="container">
      <div className="heading">
        <div className="image">
          <img src={image} alt="Student" />
          <h1>Student Login</h1>
        </div>
      </div>

      <input
        type="text"
        id="username"
        className="input"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="password"
        id="password"
        className="input"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <div className="submit">
        <div className="button" onClick={handleLogin}>
          Login
        </div>
      </div>

      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
}
