// JobDetails.js
import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useJobContext } from '../hooks/JobContext'; // Update the path
import { useHistory } from 'react-router-dom';


const JobDetails = () => {
  const { id } = useParams();
  const { job, setJob, allJobs, setAllJobs } = useJobContext();
  const history = useHistory();

  useEffect(() => {
    // Fetch all jobs from the context when the component mounts
    const fetchJobs = async () => {
      try {
        const response = await fetch('http://localhost:4444/jobs');
        if (response.ok) {
          const data = await response.json();
          setAllJobs(data.jobs);
        } else {
          console.error('Failed to fetch all jobs');
        }
      } catch (error) {
        console.error('Error fetching all jobs:', error.message);
      }
    };

    // Fetch jobs
    fetchJobs();

    // If an ID is specified, fetch details for that specific job
    if (id) {
      const currentJob = allJobs.find((job) => job._id === id);
      setJob(currentJob);
    }
  }, [id, setJob, setAllJobs]);

  if (id && !job) {
    return <div>Loading job details...</div>;
  }

  const handleUpdate = async (jobId) => {
    // Implement your update logic here, for example, redirect to an update page
    history.push(`/update-job/${jobId}`);
  };

  const handleDelete = async (jobId) => {
    try {
      // Perform the delete operation for the given job ID
      const response = await fetch(`http://localhost:4444/jobs/${jobId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Handle success, e.g., remove the deleted job from the state
        setAllJobs((prevJobs) => prevJobs.filter((job) => job._id !== jobId));
        console.log('Job deleted successfully');
      } else {
        console.error('Failed to delete job');
      }
    } catch (error) {
      console.error('Error deleting job:', error.message);
    }
  };

  return (
    <div className="homedetail">
      {id && (
        <div className="job-details">  
          <h4>{job.jobTitle}</h4>
          {/* Render other job details here */}
        </div>
      )}

      <div className="all-jobs">
        <h2>All Jobs</h2>
        <table>
          <thead>
            <tr>
              <th>Job Title</th>
              <th>Job Type</th>
              <th>Job Description</th>
              <th>Company Name</th>
              <th>Company URL</th>
              <th>Work Type</th>
              <th>Pay Scale</th>
              <th>Eligible Branches</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allJobs.map((job) => (
              <tr key={job._id}>
                <td>{job.jobTitle}</td>
                <td>{job.jobType}</td>
                <td>{job.jobDescription}</td>
                <td>{job.companyName}</td>
                <td>{job.companyURL}</td>
                <td>{job.workType}</td>
                <td>{job.payScale}</td>
                <td>{job.branchesEligible.join(', ')}</td>
                <td>
                  <span
                    role="img"
                    aria-label="Update"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleUpdate(job._id)}
                  >
                    ‚úèÔ∏è
                  </span>
                  <span
                    role="img"
                    aria-label="Delete"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleDelete(job._id)}
                  >
                    üóëÔ∏è
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default JobDetails;
