import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';

const SkillDevelopmentDetails = () => {
  const { id } = useParams();
  const { skillDevelopment, setSkillDevelopment } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courseinstructor: '',
    courselink: '',
    coursetype: '',
  });

  useEffect(() => {
    const fetchSkillDevelopmentById = async () => {
      try {
        if (id) {
          const response = await fetch(`http://localhost:4444/skill-development/${id}`);
          if (response.ok) {
            const data = await response.json();
            setSkillDevelopment(data);
            setFormData({
              title: data.title,
              description: data.description,
              courseinstructor: data.courseinstructor,
              courselink: data.courselink,
              coursetype: data.coursetype,
            });
          } else {
            console.error('Failed to fetch skill development by ID');
          }
        }
      } catch (error) {
        console.error('Error fetching skill development by ID:', error.message);
      }
    };

    if (id) {
      fetchSkillDevelopmentById();
    }
  }, [id, setSkillDevelopment]);

  const handleAddOrUpdate = async () => {
    try {
      // Your logic to add or update the content in the database

      // After successfully adding or updating, fetch the updated data
      await fetchSkillDevelopmentById();

      console.log('Skill Development content added or updated successfully');
    } catch (error) {
      console.error('Error adding or updating skill development:', error.message);
    }
  };

  if (!id) {
    return <div>No skill development ID provided.</div>;
  }

  if (!skillDevelopment) {
    return <div>Loading skill development details...</div>;
  }

  return (
    <div className="homedetails">
      <div className="skill-development-details">
        <div className="all-internships">
          <h2>Skill Development Details</h2>
          <div className="internship-card">
            <h3>{skillDevelopment.title}</h3>
            <p>{skillDevelopment.description}</p>
            <p>{`Instructor: ${skillDevelopment.courseinstructor}`}</p>
            <strong>Type:</strong>
            <p>{skillDevelopment.coursetype}</p>
          </div>
        </div>
      </div>
      {/* ... Other parts of the code */}
      <button onClick={handleAddOrUpdate}>Add or Update Content</button>
    </div>
  );
};

export default SkillDevelopmentDetails;
