import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const JobDetails = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  useEffect(() => {
    // Fetch the job details by its ID from the backend
    const fetchJobDetails = async () => {
      try {
        const response = await fetch(`http://localhost:4444/jobs/${id}`);
        if (response.ok) {
          const data = await response.json();
          setJob(data.job);
        } else {
          console.error('Failed to fetch job details');
        }
      } catch (error) {
        console.error('Error fetching job details:', error);
      }
    };

    fetchJobDetails();
  }, [id]);

  if (!job) {
    return <div>Loading job details...</div>;
  }

  return (
    <div className="home">
      <div className="job-details">
        <h4>{job.jobTitle}</h4>
        <p>Company: {job.companyName}</p>
        <p>Type: {job.jobType}</p>
        <p>Description: {job.jobDescription}</p>
        <p>Work Type: {job.workType}</p>
        <p>Pay Scale: {job.payScale}</p>
        <p>Skills Required: {job.skills.join(', ')}</p>
        {/* Add any additional styling or elements as needed */}
        <span>Close</span>
      </div>
      {/* Additional content or components for the right side */}
    </div>
  );
};

export default JobDetails;
