import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/jobpage.css'; // Import the CSS file

const JobDetails = () => {
  const { id } = useParams();
  const [job, setJob] = useState(null);
  const [allJobs, setAllJobs] = useState([]);

  useEffect(() => {
    // Fetch the job details by its ID from the backend
    const fetchJobDetails = async () => {
      try {
        const response = await fetch(`http://localhost:4444/jobs/${id}`);
        if (response.ok) {
          const data = await response.json();
          setJob(data.job);
        } else {
          console.error('Failed to fetch job details');
        }
      } catch (error) {
        console.error('Error fetching job details:', error);
      }
    };

    // Fetch all jobs
    const fetchAllJobs = async () => {
      try {
        const response = await fetch('http://localhost:4444/jobs');
        if (response.ok) {
          const data = await response.json();
          setAllJobs(data.jobs);
        } else {
          console.error('Failed to fetch all jobs');
        }
      } catch (error) {
        console.error('Error fetching job details:', error.message); // Log the error message
      }

    };

    fetchJobDetails();
    fetchAllJobs();
  }, [id]);

  if (!job) {
    return <div>Loading job details...</div>;
  }

  return (
    <div className="homedetail">
      <div className="job-details">
        <h4>{job.jobTitle}</h4>
        <p>Company: {job.companyName}</p>
        <p>Type: {job.jobType}</p>
        <p>Description: {job.jobDescription}</p>
        <p>Work Type: {job.workType}</p>
        <p>Pay Scale: {job.payScale}</p>
        <p>Skills Required: {job.skills.join(', ')}</p>
        <p>Company URL: {job.companyURL}</p>
      </div>
      {/* Additional content or components for the right side */}
      <div className="all-jobs">
        <h2>All Jobs</h2>
        <ul>
          {allJobs.map((job) => (
            <li key={job._id}>{job.jobTitle}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default JobDetails;
