// JobDetails.js
import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useJobContext } from './path-to-JobContext'; // Update the path

const JobDetails = () => {
  const { id } = useParams();
  const { job, setJob, allJobs, setAllJobs } = useJobContext();

  useEffect(() => {
    // Fetch all jobs from the context
    setAllJobs(allJobs);

    // If an ID is specified, fetch details for that specific job
    if (id) {
      const currentJob = allJobs.find((job) => job._id === id);
      setJob(currentJob);
    }
  }, [id, allJobs, setJob, setAllJobs]);

  if (id && !job) {
    return <div>Loading job details...</div>;
  }

  return (
    <div className="homedetail">
      {id && (
        <div className="job-details">
          <h4>{job.jobTitle}</h4>
          {/* Render other job details here */}
        </div>
      )}

      <div className="all-jobs">
        <h2>All Jobs</h2>
        <table>
          <thead>
            <tr>
              <th>Job Title</th>
              <th>Job Type</th>
              <th>Job Description</th>
              <th>Company Name</th>
              <th>Company URL</th>
              <th>Work Type</th>
              <th>Pay Scale</th>
              <th>Eligible Branches</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allJobs.map((job) => (
              <tr key={job._id}>
                <td>{job.jobTitle}</td>
                <td>{job.jobType}</td>
                <td>{job.jobDescription}</td>
                <td>{job.companyName}</td>
                <td>{job.companyURL}</td>
                <td>{job.workType}</td>
                <td>{job.payScale}</td>
                <td>{job.branchesEligible.join(', ')}</td>
                <td>
                  <span
                    role="img"
                    aria-label="Update"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleUpdate(job._id)}
                  >
                    ✏️
                  </span>
                  <span
                    role="img"
                    aria-label="Delete"
                    style={{
                      cursor: 'pointer',
                      marginBottom: '3.5px',
                      borderRadius: '40%',
                      backgroundColor: '#e0e0e0',
                      fontSize: '20px',
                    }}
                    onClick={() => handleDelete(job._id)}
                  >
                    🗑️
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default JobDetails;
