// SkillDevelopmentForm.js

import React, { useState, useEffect } from 'react';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';

const SkillDevelopmentForm = () => {
  const { setAllSkillDevelopments, skillDevelopment, isEditing, setIsEditing } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courselink: [],
    pdfs: [],
    images: [],
    textContent: [],
  });

  useEffect(() => {
    if (isEditing && skillDevelopment) {
      setFormData({
        title: skillDevelopment.title,
        description: skillDevelopment.description,
        courselink: skillDevelopment.courselink,
        pdfs: skillDevelopment.pdfs,
        images: skillDevelopment.images,
        textContent: skillDevelopment.textContent,
      });
    }
  }, [isEditing, skillDevelopment]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const skillDevelopmentData = {
      title: formData.title,
      description: formData.description,
      courselink: formData.courselink,
      pdfs: formData.pdfs,
      images: formData.images,
      textContent: formData.textContent,
    };

    try {
      let response;
      if (isEditing) {
        response = await fetch(`http://localhost:4444/skill-development/update-skill-development/${skillDevelopment._id}`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(skillDevelopmentData),
        });
      } else {
        response = await fetch('http://localhost:4444/skill-development/add-skill-development', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(skillDevelopmentData),
        });
      }

      if (response.ok) {
        const allSkillDevelopmentsResponse = await fetch('http://localhost:4444/skill-development');
        if (allSkillDevelopmentsResponse.ok) {
          const data = await allSkillDevelopmentsResponse.json();
          setAllSkillDevelopments(data.skillDevelopments);
        } else {
          console.error('Failed to fetch all skill developments');
        }

        setFormData({
          title: '',
          description: '',
          courselink: [],
          pdfs: [],
          images: [],
          textContent: [],
        });
        setIsEditing(false);

        console.log('Skill Development content added or updated successfully');
      } else {
        console.error('Failed to add or update skill development content');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="contained">
      <div className="skill-form">
        <h1>{isEditing ? 'Update Skill Development Content' : 'Add New Skill Development Content'}</h1>
        <form className="create" onSubmit={handleSubmit}>
          {/* Add form fields for title, description, courselink, pdfs, images, textContent here */}

          <button type="submit">{isEditing ? 'Update Skill Development Content' : 'Add Skill Development Content'}</button>
        </form>
      </div>
    </div>
  );
};

export default SkillDevelopmentForm;
