import React, { useState, useEffect } from 'react';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';

const SkillDevelopmentForm = () => {
  const {
    setAllSkillDevelopments,
    skillDevelopment,
    isEditing,
    setIsEditing,
  } = useSkillDevelopmentContext();

  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courselink: '',
    image: '',
    type: '', // Include 'type' field
  });

  useEffect(() => {
    if (isEditing && skillDevelopment) {
      setFormData({
        title: skillDevelopment.title,
        description: skillDevelopment.description,
        courselink: skillDevelopment.courselink.map((link) => `${link.title}: ${link.link}`).join(', '),
        image: skillDevelopment.image.map((image) => `${image.title}: ${image.imageUrl}`).join(', '),
        type: skillDevelopment.type, // Set 'type' field
      });
    }
  }, [isEditing, skillDevelopment]);

  const handleArrayChange = (field, e) => {
    const values = e.target.value.split(',').map((value) => value.trim());
    setFormData({ ...formData, [field]: values });
  };

  const handleResponse = async (response) => {
    if (response.ok) {
      const allSkillDevelopmentsResponse = await fetch('http://localhost:4444/skill-development');
      if (allSkillDevelopmentsResponse.ok) {
        const data = await allSkillDevelopmentsResponse.json();
        setAllSkillDevelopments(data.skillDevelopments);
      } else {
        console.error('Failed to fetch all skill developments');
      }

      setFormData({
        title: '',
        description: '',
        courselink: '',
        image: '',
        type: '', // Reset 'type' field
      });
      setIsEditing(false);

      console.log('Skill Development content added or updated successfully');
    } else {
      console.error('Failed to add or update skill development content');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append('title', formData.title);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('courselink', JSON.stringify(formData.courselink));
    formDataToSend.append('image', file);
    formDataToSend.append('type', formData.type); // Include 'type' in form data

    try {
      let response;
      if (isEditing) {
        response = await fetch(`http://localhost:4444/skill-development/update-skill-development/${skillDevelopment._id}`, {
          method: 'PATCH',
          body: formDataToSend,
        });
      } else {
        response = await fetch('http://localhost:4444/skill-development/add-skill-development', {
          method: 'POST',
          body: formDataToSend,
        });
      }

      handleResponse(response);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="contained">
      <div className="skill-form">
        <h1>{isEditing ? 'Update Skill Development Content' : 'Add New Skill Development Content'}</h1>
        <form className="create" onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group">
              <label>Title:</label>
              <input
                type="text"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Description:</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              ></textarea>
            </div>
          </div>

          <div className="form-group">
            <label>Course Link:</label>
            <input
              type="text"
              className="input-field"
              value={formData.courselink}
              onChange={(e) => handleArrayChange('courselink', e)}
              placeholder="(e.g., Course Title: Link)"
              required
            />
          </div>

          <div className="form-group">
            <label>Image:</label>
            <input type="file" onChange={handleFileChange} accept="image/*" />
          </div>

          <div className="form-group">
            <label>Type:</label>
            <input
              type="text"
              value={formData.type}
              onChange={(e) => setFormData({ ...formData, type: e.target.value })}
            />
          </div>

          <button type="submit">
            {isEditing ? 'Update Skill Development Content' : 'Add Skill Development Content'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SkillDevelopmentForm;
