import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory from React Router
import '../otherstyles/admin.css';
import image from '../images/admin.webp';

export default function Admin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [action, setAction] = useState('AdminLogin');
  const [error, setError] = useState('');
  const history = useHistory(); // Create a history object

// components/AdminLogin.js
const handleLogin = async () => {
  try {
    const response = await fetch('http://localhost:4444/admin/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    if (response.ok) {
      const data = await response.json();
      localStorage.setItem('token', data.token);
      history.push('http://localhost:4444/admin/dashboard');
    } else {
      setError('Invalid credentials');
    }
  } catch (error) {
    console.error(error);
  }
};


  return (
    <div className="container">
      <div className="heading">
        <div className="image">
          <img src={image} alt="Some Description" />
          <h1>{action}</h1>
        </div>
      </div>

      <input
        type="text"
        id="username"
        className="input"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <input
        type="password"
        id="password"
        className="input"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      {error && <div className="error-message">{error}</div>}

      <div className="submit">
        <div onClick={handleLogin}>Login</div>
      </div>
    </div>
  );
}
