import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/jobpage.css'; // Import the CSS file
import Header from './Header'; // Import the Header component

const StudentInternshipDetails = () => {
  const { id } = useParams();
  const [internship, setInternship] = useState(null);
  const [allInternships, setAllInternships] = useState([]);
  const [selectedBranch, setSelectedBranch] = useState('ALL');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchAllInternships = async () => {
      try {
        const response = await fetch('http://localhost:4444/internships');
        if (response.ok) {
          const data = await response.json();
          setAllInternships(data.internships);
        } else {
          console.error('Failed to fetch all internships');
        }
      } catch (error) {
        console.error('Error fetching all internships:', error.message);
      }
    };

    if (id) {
      const fetchInternshipDetails = async () => {
        try {
          const response = await fetch(`http://localhost:4444/internships/${id}`);
          if (response.ok) {
            const data = await response.json();
            setInternship(data.internship);
          } else {
            console.error('Failed to fetch internship details');
          }
        } catch (error) {
          console.error('Error fetching internship details:', error.message);
        }
      };

      fetchInternshipDetails();
    }

    fetchAllInternships();
  }, [id]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleBranchChange = (e) => {
    setSelectedBranch(e.target.value);
  };

  const filteredInternships = allInternships
    .filter((internship) =>
      (selectedBranch === 'ALL' ||
        (internship.eligibleBranches && internship.eligibleBranches.includes(selectedBranch))) &&
      internship.companyName.toLowerCase().includes(searchQuery.toLowerCase())
    );

  if (id && !internship) {
    return <div>Loading internship details...</div>;
  }

  return (
    <div className="homedetailsofstudent">
      {id && (
        <div className="internship-detailsofstudents">
          <h4>{internship.internshipTitle}</h4>
          {/* Render other internship details here */}
        </div>
      )}

      <div className="all-internshipsofstudents">
        <h2>All Internships</h2>

        {/* Use the Header component for displaying selected branches */}
        <Header
          keywords={[selectedBranch]}
          removeKeywords={() => setSelectedBranch('ALL')}
          clearAll={() => setSelectedBranch('ALL')}
        />

        <div className="search-container">
          <div className="search-bar">
            <p>
              <strong>Search Here:</strong>
            </p>
            <input
              type="text"
              placeholder="Search by Company Name"
              value={searchQuery}
              onChange={handleSearchChange}
            />
          </div>
        </div>

        <div className="branch-filter">
          <p>
            <strong>Filter by Branch:</strong>
          </p>
          <label>
            <input
              type="radio"
              value="ALL"
              checked={selectedBranch === 'ALL'}
              onChange={handleBranchChange}
            />
            ALL
          </label>
          <label>
            <input
              type="radio"
              value="CSE"
              checked={selectedBranch === 'CSE'}
              onChange={handleBranchChange}
            />
            CSE
          </label>
          {/* Add radio buttons for other branches */}
          {/* Example: */}
          <label>
            <input
              type="radio"
              value="CET"
              checked={selectedBranch === 'CET'}
              onChange={handleBranchChange}
            />
            CET
          </label>
          {/* Repeat this for other branches */}
        </div>

        <div className="internship-cardsofstudents">
          {filteredInternships.map((internship) => (
            <div key={internship._id} className="internship-cardofstudents">
              <h3>{internship.internshipTitle}</h3>
              <p>
                <strong>Internship Type:</strong> {internship.internshipType}
              </p>
              <p>
                <strong>Description:</strong> {internship.internshipDescription}
              </p>
              <p>
                <strong>Company:</strong> {internship.companyName}
              </p>
              <p>
                <strong>Duration:</strong> {internship.duration}
              </p>
              <p>
  <strong>Eligible branches:</strong>{' '}
  {internship.eligibleBranches !== undefined && internship.eligibleBranches !== null
    ? internship.eligibleBranches.join(', ')
    : 'Not specified'}
</p>


              <a
                href={internship.companyURL}
                target="_blank"
                rel="noopener noreferrer"
                className="apply-button"
              >
                Apply Now!
              </a>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default StudentInternshipDetails;
