import React, { createContext, useContext, useState, useEffect } from 'react';

const InternshipContext = createContext();

export const InternshipProvider = ({ children }) => {
  const [internship, setInternship] = useState(null);
  const [allInternships, setAllInternships] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4444/internships');
        if (response.ok) {
          const data = await response.json();
          setAllInternships(data.internships);
        } else {
          console.error('Failed to fetch all internships');
        }
      } catch (error) {
        console.error('Error fetching all internships:', error.message);
      }
    };

    fetchData();
  }, []); // Fetch all internships when the context is created

  const contextValue = {
    internship,
    setInternship,
    allInternships,
    setAllInternships,
  };

  return (
    <InternshipContext.Provider value={contextValue}>
      {children}
    </InternshipContext.Provider>
  );
};

export const useInternshipContext = () => {
  return useContext(InternshipContext);
};
