import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { useSkillDevelopmentContext } from '../hooks/SkillDevelopmentContext';
import SkillDevelopmentForm from './SkillDevelopmentForm';

const SkillDevelopmentDetails = () => {
  const { id } = useParams();
  const {
    skillDevelopment,
    setSkillDevelopment,
    allSkillDevelopments,
    setAllSkillDevelopments,
    isEditing,
    setIsEditing,
  } = useSkillDevelopmentContext();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    courseinstructor: '',
    courselink: '',
    coursetype: '', // Add 'type' field
  });

  const history = useHistory();

  useEffect(() => {
    const fetchSkillDevelopments = async () => {
      try {
        const response = await fetch('http://localhost:4444/skill-development');
        if (response.ok) {
          const data = await response.json();
          setAllSkillDevelopments(data.skillDevelopments);
        } else {
          console.error('Failed to fetch all skill developments');
        }
      } catch (error) {
        console.error('Error fetching all skill developments:', error.message);
      }
    };

    fetchSkillDevelopments();

    if (id) {
      const currentSkillDevelopment = allSkillDevelopments.find(
        (skillDev) => skillDev._id === id
      );
      if (currentSkillDevelopment) {
        setSkillDevelopment(currentSkillDevelopment);

        setFormData({
          title: currentSkillDevelopment.title,
          description: currentSkillDevelopment.description,
          courseinstructor: currentSkillDevelopment.courseinstructor,
          courselink: currentSkillDevelopment.courselink,
          coursetype: currentSkillDevelopment.coursetype,
        });
      }
    }
  }, [id, setSkillDevelopment, setAllSkillDevelopments]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleUpdate = async (skillDevelopmentId) => {
    setIsEditing(true);

    try {
      const response = await fetch(
        `http://localhost:4444/skill-development/update-skill-development/${skillDevelopmentId}`,
        {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            title: formData.title,
            description: formData.description,
            courseinstructor: formData.courseinstructor,
            courselink: formData.courselink.split(',').map((link) => link.trim()),
            coursetype: formData.coursetype,
          }),
        }
      );

      if (response.ok) {
        const updatedSkillDevelopment = await response.json();
        history.push('/admin/skillpage');
        setSkillDevelopment(updatedSkillDevelopment);
      } else {
        console.error('Failed to update skill development:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating skill development:', error.message);
    }
  };

  const handleDelete = async (skillDevelopmentId) => {
    try {
      const response = await fetch(
        `http://localhost:4444/skill-development/${skillDevelopmentId}`,
        {
          method: 'DELETE',
        }
      );

      if (response.ok) {
        setAllSkillDevelopments((prevSkillDevs) =>
          prevSkillDevs.filter((skillDev) => skillDev._id !== skillDevelopmentId)
        );
        console.log('Skill Development deleted successfully');
      } else {
        console.error('Failed to delete skill development');
      }
    } catch (error) {
      console.error('Error deleting skill development:', error.message);
    }
  };

  return (
    <div className="homedetails">
      {id && (
        <div className="skill-development-details">
          {isEditing ? (
            <SkillDevelopmentForm
              isEditing={isEditing}
              setIsEditing={setIsEditing}
              initialFormData={formData}
            />
          ) : (
            <div>
              {skillDevelopment ? (
                <>
                  <h4>{skillDevelopment.title}</h4>
                  <p>{skillDevelopment.description}</p>
                  <p>{`Instructor: ${formData.courseinstructor}`}</p>
                  <div>
                    <strong>Course Links:</strong>
                    {skillDevelopment.courselink.length > 0 ? (
                      <ul>
                        {skillDevelopment.courselink.map((link, index) => (
                          <li key={index}>
                            <span>{link.title}: </span>
                            <a
                              href={link.link}
                              target="_blank"
                              rel="noopener noreferrer"
                            >
                              {link.link}
                            </a>
                          </li>
                        ))}
                      </ul>
                    ) : (
                      <p>No course links available.</p>
                    )}
                  </div>
                  <div>
                    <strong>Type:</strong>
                    <p>{formData.coursetype}</p>
                  </div>
                  {/* ... Other parts of the code */}
                </>
              ) : (
                <p>Loading skill development details...</p>
              )}
            </div>
          )}
          <button
            onClick={
              isEditing
                ? () => handleUpdate(skillDevelopment._id)
                : handleEditClick
            }
          >
            {isEditing ? 'Save' : 'Edit'}
          </button>
        </div>
      )}

      {/* ... Other parts of the code */}
    </div>
  );
};

export default SkillDevelopmentDetails;
